import React from "react";
import Slider from "react-slick";
import {
  FaAws,
  FaDocker,
  FaGitAlt,
  FaPython,
} from "react-icons/fa";
import {
  SiTerraform,
  SiGrafana,
  SiMongodb,
  SiBlazor,
  SiReact,
  SiUnity,
  SiKubernetes,
  SiC,
  SiDjango,
} from "react-icons/si";
import { GiCube } from "react-icons/gi";
import { TbBrandSupabase } from "react-icons/tb";
import { MdOutlineScience } from "react-icons/md";
import { ReactComponent as PlaywrightIcon } from "../img/playwright.svg";
import myImage from "../img/myImage.png";
import "./About.css";

import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

class About extends React.Component {
  constructor() {
    super();
    this.sliderRef = React.createRef();
    this.state = {
      skills: [
        { name: "C", icon: <SiC color="#00599C" size={70} />, bg: "linear-gradient(135deg,#e0f7fa,#00599C)" },
        { name: "C++", icon: <SiC color="#004482" size={70} />, bg: "linear-gradient(135deg,#fff,#004482)" },
        { name: "C#", icon: <SiC color="#239120" size={70} />, bg: "linear-gradient(135deg,#e8f5e9,#239120)" },
        { name: "Python", icon: <FaPython color="#3776AB" size={70} />, bg: "linear-gradient(135deg,#ffe873,#3776ab)" },
        { name: "AWS", icon: <FaAws color="#FF9900" size={70} />, bg: "linear-gradient(135deg,#232f3e,#ff9900)" },
        { name: "Git/GitHub", icon: <FaGitAlt color="#F05032" size={70} />, bg: "linear-gradient(135deg,#24292e,#f05032)" },
        { name: "GitHub Actions", icon: <FaGitAlt color="#4078c0" size={70} />, bg: "linear-gradient(135deg,#232F3E,#4078c0)" },
        { name: "Jenkins", icon: <FaGitAlt color="#d24939" size={70} />, bg: "linear-gradient(135deg,#000,#d24939)" },
        { name: "Terraform", icon: <SiTerraform color="#623CE4" size={70} />, bg: "linear-gradient(135deg,#000,#623ce4)" },
        { name: "Docker", icon: <FaDocker color="#2496ED" size={70} />, bg: "linear-gradient(135deg,#0db7ed,#fff)" },
        { name: "Kubernetes", icon: <SiKubernetes color="#326CE5" size={70} />, bg: "linear-gradient(135deg,#172b4d,#326CE5)" },
        { name: "Grafana", icon: <SiGrafana color="#F46800" size={70} />, bg: "linear-gradient(135deg,#1a1a1a,#f46800)" },
        { name: "Prometheus", icon: <SiGrafana color="#e6522c" size={70} />, bg: "linear-gradient(135deg,#1a1a1a,#e6522c)" },
        { name: "Raygun", icon: <SiGrafana color="#00c7b7" size={70} />, bg: "linear-gradient(135deg,#1e1e1e,#00c7b7)" },
        { name: "SonarCloud", icon: <SiGrafana color="#f3702a" size={70} />, bg: "linear-gradient(135deg,#000,#f3702a)" },
        { name: "Django", icon: <SiDjango color="#092E20" size={70} />, bg: "linear-gradient(135deg,#fff,#092E20)" },
        { name: ".NET Framework", icon: <SiBlazor color="#512BD4" size={70} />, bg: "linear-gradient(135deg,#ede7f6,#512bd4)" },
        { name: "Blazor", icon: <SiBlazor color="#512BD4" size={70} />, bg: "linear-gradient(135deg,#ede7f6,#512bd4)" },
        { name: "MudBlazor", icon: <SiBlazor color="#6a1b9a" size={70} />, bg: "linear-gradient(135deg,#f3e5f5,#6a1b9a)" },
        { name: "React.js", icon: <SiReact color="#61DAFB" size={70} />, bg: "linear-gradient(135deg,#20232a,#61dafb)" },
        { name: "Streamlit", icon: <MdOutlineScience color="#FF4B4B" size={70} />, bg: "linear-gradient(135deg,#fff,#ff4b4b)" },
        { name: "Supabase", icon: <TbBrandSupabase color="#3ecf8e" size={70} />, bg: "linear-gradient(135deg,#000,#3ecf8e)" },
        { name: "MongoDB", icon: <SiMongodb color="#47A248" size={70} />, bg: "linear-gradient(135deg,#fff,#47a248)" },
        { name: "Unity Engine", icon: <SiUnity color="#000" size={70} />, bg: "linear-gradient(135deg,#fff,#000)" },
        { name: "Blender", icon: <GiCube color="#E87D0D" size={70} />, bg: "linear-gradient(135deg,#000,#e87d0d)" },
        { name: "Playwright", icon: <PlaywrightIcon width={70} height={70} />, bg: "linear-gradient(135deg,#000,#2ead33)" },
      ],
      flipping: false
    };
  }

  handleFlipStart = () => {
    this.setState({ flipping: true }, () => {
      this.sliderRef.current.slickPause();
    });
  };

  handleFlipEnd = () => {
    this.setState({ flipping: false }, () => {
      this.sliderRef.current.slickPlay();
    });
  };

  render() {
    const settings = {
      infinite: true,
      autoplay: true,
      autoplaySpeed: 1,
      speed: 6000,
      cssEase: "linear",
      slidesToShow: 7,
      slidesToScroll: 1,
      arrows: false,
      pauseOnHover: false,
      draggable: true,
      responsive: [
        { breakpoint: 1400, settings: { slidesToShow: 6 } },
        { breakpoint: 1200, settings: { slidesToShow: 5 } },
        { breakpoint: 992, settings: { slidesToShow: 4 } },
        { breakpoint: 768, settings: { slidesToShow: 3 } },
        { breakpoint: 576, settings: { slidesToShow: 2 } },
        { breakpoint: 480, settings: { slidesToShow: 1 } },
      ],
    };

    return (
      <section id="about" className="about-mf sect-pt4 route">
  <div className="container-fluid px-5">
    <div className="row align-items-center mb-5">
      {/* Profile Image */}
      <div className="col-lg-4 col-md-5 d-flex justify-content-center mb-4 mb-md-0">
        <img
          src={myImage}
          alt="Profile"
          className="img-fluid rounded-circle shadow-lg profile-img"
        />
      </div>

      {/* About Me */}
      <div className="col-lg-8 col-md-7">
        <div className="about-me-card p-4 shadow-lg">
          <h3 className="about-title mb-3">About Me</h3>
          <ul className="about-points">
            <li>💡 <strong>Computer Science Undergraduate</strong> at <em>[Your College Name]</em></li>
            <li>☁️ Passionate about <strong>Cloud, DevOps & Full Stack Development</strong></li>
            <li>🔥 Known for being <strong>curious, disciplined & proactive</strong></li>
            <li>🤝 Thrive in <strong>collaborative environments</strong> & always adaptable</li>
            <li>🚀 Described as <strong>innovative, detail-oriented & dependable</strong></li>
          </ul>
        </div>
      </div>
    </div>

    {/* Skills Slider */}
    <div className="row">
      <div className="col-12">
        <h5 className="skills-title mb-4">My Skills</h5>
        <Slider ref={this.sliderRef} {...settings} className="skills-slider">
          {this.state.skills.map((skill, idx) => (
            <div
              key={idx}
              className="skill-slide d-flex justify-content-center"
              onMouseEnter={this.handleFlipStart}
              onMouseLeave={this.handleFlipEnd}
            >
              <div className="skill-card">
                <div className="skill-icon">{skill.icon}</div>
                <div className="skill-name">{skill.name}</div>
              </div>
            </div>
          ))}
        </Slider>
      </div>
    </div>
  </div>
</section>

    );
  }
}

export default About;
