import React, { useState, useEffect, useRef } from "react";
import "./Navbar.css";

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const menuRef = useRef(null);

  useEffect(() => {
    // close when clicking outside or pressing Escape
    const handleOutside = (e) => {
      if (menuRef.current && !menuRef.current.contains(e.target)) {
        setIsOpen(false);
      }
    };
    const handleEsc = (e) => {
      if (e.key === "Escape") setIsOpen(false);
    };

    document.addEventListener("mousedown", handleOutside);
    document.addEventListener("keydown", handleEsc);
    return () => {
      document.removeEventListener("mousedown", handleOutside);
      document.removeEventListener("keydown", handleEsc);
    };
  }, []);

  const menuItems = [
    { id: "home", label: "Home" },
    { id: "about", label: "About" },
    { id: "work", label: "Work" },
    { id: "contact", label: "Contact" },
  ];

  const radius = 120; // px: adjust arc radius

  const scrollToSection = (e, id) => {
    e.preventDefault();
    const el = document.querySelector(id);
    if (el) {
      el.scrollIntoView({ behavior: "smooth", block: "start" });
    }
    setIsOpen(false);
  };

  return (
    <div className="menu-container top-right" ref={menuRef} aria-hidden={false}>
      <button
        type="button"
        className={`menu-btn cosmic ${isOpen ? "open" : ""}`}
        aria-expanded={isOpen}
        aria-label={isOpen ? "Close menu" : "Open menu"}
        onClick={() => setIsOpen((v) => !v)}
      >
        <span></span>
        <span></span>
        <span></span>
      </button>

      <ul className={`circular-menu`} aria-hidden={!isOpen}>
        {menuItems.map((item, i) => {
          const total = Math.max(menuItems.length - 1, 1);
          // angle from 0 -> 90 degrees mapped to left -> down quadrant
          const angle = (i / total) * (Math.PI / 2); // 0 .. PI/2
          const x = -Math.cos(angle) * radius; // negative = left
          const y = Math.sin(angle) * radius;  // positive = down

          const style = {
            transform: isOpen
              ? `translate(${x}px, ${y}px) scale(1)`
              : `translate(0px, 0px) scale(0.85)`,
            transitionDelay: `${isOpen ? i * 40 : (menuItems.length - i) * 20}ms`,
            pointerEvents: isOpen ? "auto" : "none",
          };

          return (
            <li key={item.id} style={style}>
              <a
                href={`#${item.id}`}
                onClick={(e) => scrollToSection(e, `#${item.id}`)}
                aria-label={item.label}
              >
                {item.label}
              </a>
            </li>
          );
        })}
      </ul>
    </div>
  );
};

export default Navbar;
