import React from "react";
import Slider from "react-slick";
import {
  FaAws, FaDocker, FaGitAlt, FaJava, FaPython,

} from "react-icons/fa";
import {
  SiTerraform, SiGrafana, SiMongodb, SiBlazor, SiSupabase,
  SiReact, SiUnity, SiKubernetes
} from "react-icons/si";
import { GiCube } from "react-icons/gi"; // Blender icon substitute
import { TbBrandSupabase } from "react-icons/tb"; // Supabase alt
import { MdOutlineScience } from "react-icons/md"; // Streamlit placeholder
import { SiPlaywright } from "react-icons/si";
import { SiC } from 'react-icons/si';
import { FaCode } from 'react-icons/fa'; // Generic code icon
import { ReactComponent as PlaywrightIcon } from "../img/playwright.svg";
import myImage from "../img/myImage.png";
import "./About.css";
import { SiCsharp } from "react-icons/si";
import { SiTestinglibrary } from "react-icons/si";
class About extends React.Component {
  constructor() {
    super();
    this.sliderRef = React.createRef();

    this.state = {
      categories: [
        {
          title: "Cloud & AWS Services",
          skills: [
            { name: "AWS", icon: <FaAws color="#FF9900" />, bg: "linear-gradient(135deg,#232f3e,#ff9900)" }
          ]
        },
        {
          title: "DevOps Tools",
          skills: [
            { name: "Git/GitHub", icon: <FaGitAlt color="#F05032" />, bg: "linear-gradient(135deg,#24292e,#f05032)" },
            { name: "GitHub Actions", icon: <FaGitAlt color="#4078c0" />, bg: "linear-gradient(135deg,#232F3E,#4078c0)" },
            { name: "Jenkins", icon: <FaGitAlt color="#d24939" />, bg: "linear-gradient(135deg,#000,#d24939)" },
            { name: "Docker", icon: <FaDocker color="#2496ED" />, bg: "linear-gradient(135deg,#0db7ed,#fff)" },
            { name: "Kubernetes", icon: <SiKubernetes color="#326CE5" />, bg: "linear-gradient(135deg,#172b4d,#326CE5)" }
          ]
        },
        {
          title: "Monitoring & Logging",
          skills: [
            { name: "Grafana", icon: <SiGrafana color="#F46800" />, bg: "linear-gradient(135deg,#1a1a1a,#f46800)" },
            { name: "Prometheus", icon: <SiGrafana color="#e6522c" />, bg: "linear-gradient(135deg,#1a1a1a,#e6522c)" },
            { name: "Raygun", icon: <SiGrafana color="#00c7b7" />, bg: "linear-gradient(135deg,#1e1e1e,#00c7b7)" },
            { name: "SonarCloud", icon: <SiGrafana color="#f3702a" />, bg: "linear-gradient(135deg,#000,#f3702a)" }
          ]
        },
        {
          title: "Infrastructure as Code",
          skills: [
            { name: "Terraform", icon: <SiTerraform color="#7B42BC" />, bg: "linear-gradient(135deg,#1e1e1e,#7b42bc)" }
          ]
        },
        {
          title: "Programming Languages",
          skills: [
            { name: "C", icon: <SiC color="#00599C" />, bg: "linear-gradient(135deg,#e0f7fa,#00599C)" },
            { name: "C++", icon: <SiC color="#004482" />, bg: "linear-gradient(135deg,#fff,#004482)" },
            { name: "C#", icon: <SiC color="#239120" />, bg: "linear-gradient(135deg,#e8f5e9,#239120)" },
            { name: "Python", icon: <FaPython color="#3776AB" />, bg: "linear-gradient(135deg,#ffe873,#3776ab)" },
            { name: "Java", icon: <FaJava color="#007396" />, bg: "linear-gradient(135deg,#fbe9e7,#007396)" }
          ]
        },
        {
          title: "Frameworks",
          skills: [
            { name: "Blazor", icon: <SiBlazor color="#512BD4" />, bg: "linear-gradient(135deg,#ede7f6,#512bd4)" },
            { name: "MudBlazor", icon: <SiBlazor color="#6a1b9a" />, bg: "linear-gradient(135deg,#f3e5f5,#6a1b9a)" },
            { name: "React.js", icon: <SiReact color="#61DAFB" />, bg: "linear-gradient(135deg,#20232a,#61dafb)" },
            { name: "Streamlit", icon: <MdOutlineScience color="#FF4B4B" />, bg: "linear-gradient(135deg,#fff,#ff4b4b)" }
          ]
        },
        {
          title: "Databases",
          skills: [
            { name: "Supabase", icon: <TbBrandSupabase color="#3ecf8e" />, bg: "linear-gradient(135deg,#000,#3ecf8e)" },
            { name: "MongoDB", icon: <SiMongodb color="#47A248" />, bg: "linear-gradient(135deg,#fff,#47a248)" }
          ]
        },
        {
          title: "Other Tools",
          skills: [
            { name: "Unity Engine", icon: <SiUnity color="#000" />, bg: "linear-gradient(135deg,#fff,#000)" },
            { name: "Blender", icon: <GiCube color="#E87D0D" />, bg: "linear-gradient(135deg,#000,#e87d0d)" },
            { name: "Playwright", icon: <SPlaywrightIcon color="#2EAD33" />, bg: "linear-gradient(135deg,#000,#2ead33)" }
          ]
        }
      ]
    };
  }

  handleMouseEnter = () => {
    if (this.sliderRef.current) this.sliderRef.current.slickPause();
  };
  handleMouseLeave = () => {
    if (this.sliderRef.current) this.sliderRef.current.slickPlay();
  };

  render() {
    const settings = {
      infinite: true,
      autoplay: true,
      autoplaySpeed: 2000,
      speed: 2000,
      slidesToShow: 5,
      slidesToScroll: 1,
      arrows: false,
      pauseOnHover: false,
      cssEase: "linear",
    };

    return (
      <section id="about" className="about-mf sect-pt4 route">
        <div className="container-fluid">
          <div className="row">
            <div className="col-sm-12">
              <div className="box-shadow-full modern-card">
                <div className="row">
                  {/* Profile Column */}
                  <div className="col-md-5 text-center">
                    <div className="about-img mb-4">
                      <img src={myImage} alt="Profile"
                        className="img-fluid rounded-circle shadow-lg profile-img" />
                    </div>
                  </div>

                  {/* About Me + Skills */}
                  <div className="col-md-7">
                    <div className="about-me pt-4 pt-md-0">
                      <div className="title-box-2 mb-4">
                        <h3 className="title-left">About Me</h3>
                      </div>
                      <p className="lead text-muted">
                        Iâ€™m Kavinraj S.K, a Computer Science undergraduate with strong foundations in Cloud, DevOps, and Full Stack Development.
                      </p>
                    </div>

                    {/* ðŸ”¹ Organized Skills Showcase */}
                    {this.state.categories.map((cat, i) => (
                      <div key={i} className="skills-showcase mt-5">
                        <h5 className="title-s mb-3">{cat.title}</h5>
                        <Slider ref={this.sliderRef} {...settings} className="skills-slider mb-4">
                          {cat.skills.map((skill, idx) => (
                            <div key={idx}
                              className="skill-slide"
                              onMouseEnter={this.handleMouseEnter}
                              onMouseLeave={this.handleMouseLeave}
                            >
                              <div className="skill-flip" style={{ background: skill.bg }}>
                                <div className="skill-front">
                                  <span className="skill-icon-large">{skill.icon}</span>
                                </div>
                                <div className="skill-back">
                                  <p>{skill.name}</p>
                                </div>
                              </div>
                            </div>
                          ))}
                        </Slider>
                      </div>
                    ))}

                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    );
  }
}

export default About;
