import React, { useState, useEffect, useRef } from "react";
import "./Navbar.css";

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const menuRef = useRef(null);

  useEffect(() => {
    const handleOutside = (e) => {
      if (menuRef.current && !menuRef.current.contains(e.target)) {
        setIsOpen(false);
      }
    };
    const handleEsc = (e) => {
      if (e.key === "Escape") setIsOpen(false);
    };

    document.addEventListener("mousedown", handleOutside);
    document.addEventListener("keydown", handleEsc);
    return () => {
      document.removeEventListener("mousedown", handleOutside);
      document.removeEventListener("keydown", handleEsc);
    };
  }, []);

  const scrollToSection = (e, id) => {
    e.preventDefault();
    const target = document.querySelector(id);
    if (target) {
      target.scrollIntoView({ behavior: "smooth", block: "start" });
      setIsOpen(false);
    }
  };

  return (
    <div className="menu-container top-right" ref={menuRef}>
      <button
        className={`menu-btn ${isOpen ? "open" : ""}`}
        aria-expanded={isOpen}
        aria-label={isOpen ? "Close menu" : "Open menu"}
        onClick={() => setIsOpen((v) => !v)}
      >
        <span></span>
        <span></span>
        <span></span>
      </button>

      <ul
        className={`menu-items ${isOpen ? "show" : ""}`}
        role="menu"
        aria-hidden={!isOpen}
      >
        <li role="none">
          <a role="menuitem" href="#home" onClick={(e) => scrollToSection(e, "#home")}>
            Home
          </a>
        </li>
        <li role="none">
          <a role="menuitem" href="#about" onClick={(e) => scrollToSection(e, "#about")}>
            About
          </a>
        </li>
        <li role="none">
          <a role="menuitem" href="#work" onClick={(e) => scrollToSection(e, "#work")}>
            Work
          </a>
        </li>
        <li role="none">
          <a role="menuitem" href="#contact" onClick={(e) => scrollToSection(e, "#contact")}>
            Contact
          </a>
        </li>
      </ul>
    </div>
  );
};

export default Navbar;
