import React, { useState, useEffect } from "react";
import stock1 from "../img/image2.jpg";
import stock2 from "../img/image2.jpg";
import stock3 from "../img/image3.jpg";
import stock4 from "../img/image4.jpg";
import stock5 from "../img/image5.jpg";
import stock6 from "../img/image6.jpg";

const projects = [
  {
    id: 1,
    title: "To-Do Web Application",
    tech: "Blazor | MongoDB | GitHub Actions | Docker | Prometheus | Grafana",
    description:
      "A modern task management application where users can create, update, and track tasks. Integrated with CI/CD pipelines, Docker deployment, and monitoring tools (Prometheus & Grafana) to ensure reliability and high performance.",
    img: stock1,
    link: "#",
  },
  {
    id: 2,
    title: "Smart ID Checker",
    tech: "FastAPI | React | OpenCV | AWS ECS",
    description:
      "Full-stack application to verify uploaded ID documents. Checks blur, brightness, contrast, and glare to ensure documents are suitable for automated identity verification systems. Deployed on AWS ECS for scalability.",
    img: stock2,
    link: "#",
  },
  {
    id: 3,
    title: "My Blog",
    tech: "MongoDB | Blazor | MudBlazor | C#",
    description:
      "A social blogging platform allowing users to post blogs, follow others, comment, and engage in discussions. Supports media uploads, real-time updates, and a follower/following system to increase engagement.",
    img: stock3,
    link: "#",
  },
  {
    id: 4,
    title: "Virtual Classroom",
    tech: "Unity | GPT-4 | Plugins",
    description:
      "An interactive classroom with GPT-4 chatbot for doubt clearance, a PDF viewer for reference, and a collaborative whiteboard to improve remote learning.",
    img: stock4,
    link: "#",
  },
  {
    id: 5,
    title: "Weather Forecast App",
    tech: "Blazor | Weather API | MongoDB",
    description:
      "Provides accurate 14-day forecasts with login and location-based updates. Clean, responsive interface ensures smooth navigation and easy-to-read visuals.",
    img: stock5,
    link: "#",
  },
  {
    id: 6,
    title: "Virtual Herbal Garden",
    tech: "Unity | Assets | Plugins",
    description:
      "A simulation app for learning about medicinal plants. Users can plant seeds, monitor growth, and learn herbal preparation. Includes interactive modules.",
    img: stock6,
    link: "#",
  },
];

const PortfolioCarousel = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const itemsPerSlide = 3; // show 3 at a time

  // Auto-slide every 6 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) =>
        prevIndex + itemsPerSlide >= projects.length ? 0 : prevIndex + itemsPerSlide
      );
    }, 6000);
    return () => clearInterval(interval);
  }, []);

  // slice the array for visible projects
  const visibleProjects = projects.slice(currentIndex, currentIndex + itemsPerSlide);

  return (
    <section id="work" className="portfolio-mf sect-pt4 route">
      <div className="container">
        <h3 className="title-a text-center">Portfolio</h3>
        <p className="subtitle-a text-center">A selection of my recent projects</p>
        <div className="line-mf mb-4"></div>

        <div className="row">
          {visibleProjects.map((project) => (
            <div key={project.id} className="col-lg-4 col-md-6 mb-4 d-flex">
              <div
                className="project-card"
                style={{
                  border: "1px solid #ddd",
                  borderRadius: "12px",
                  background: "#fff",
                  padding: "20px",
                  display: "flex",
                  flexDirection: "column",
                  justifyContent: "space-between",
                  boxShadow: "0 4px 15px rgba(0,0,0,0.1)",
                  transition: "transform 0.3s",
                  width: "100%",
                }}
              >
                <div className="img-container" style={{ borderRadius: "10px", overflow: "hidden" }}>
                  <img
                    src={project.img}
                    alt={project.title}
                    style={{
                      width: "100%",
                      height: "200px",
                      objectFit: "cover",
                      transition: "transform 0.5s ease",
                    }}
                    className="project-image"
                  />
                </div>
                <div className="mt-3">
                  <h5 style={{ fontWeight: "600" }}>{project.title}</h5>
                  <p style={{ fontSize: "0.9rem", color: "#444", fontWeight: "500" }}>
                    {project.tech}
                  </p>
                  <p style={{ fontSize: "0.85rem", color: "#666", lineHeight: "1.5" }}>
                    {project.description}
                  </p>
                </div>
                <a
                  href={project.link}
                  className="btn btn-sm btn-primary mt-2 align-self-start"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  View Project
                </a>
              </div>
            </div>
          ))}
        </div>
      </div>

      <style>
        {`
          .project-card:hover .project-image {
            transform: scale(1.08);
          }
          .project-card:hover {
            transform: translateY(-5px);
            box-shadow: 0 10px 25px rgba(0,0,0,0.2);
          }
        `}
      </style>
    </section>
  );
};

export default PortfolioCarousel;
