import React, { useEffect, useState } from "react";
import stock1 from "../img/image2.jpg";
import stock2 from "../img/image2.jpg";
import stock3 from "../img/image3.jpg";
import stock4 from "../img/image4.jpg";
import stock5 from "../img/image5.jpg";
import stock6 from "../img/image6.jpg";

const projects = [
  {
    id: 1,
    title: "To-Do Web Application",
    tech: "Blazor | MongoDB | GitHub Actions | Docker | Prometheus | Grafana",
    description:
      "A modern task management application with CI/CD pipelines, Docker deployment, and monitoring tools. Features prioritization, completion tracking, and notifications.",
    img: stock1,
    link: "#",
  },
  {
    id: 2,
    title: "Smart ID Checker",
    tech: "FastAPI | React | OpenCV | AWS ECS",
    description:
      "Verifies uploaded ID documents by checking blur, brightness, contrast, and glare. Deployed on AWS ECS for scalability.",
    img: stock2,
    link: "#",
  },
  {
    id: 3,
    title: "My Blog",
    tech: "MongoDB | Blazor | MudBlazor | C#",
    description:
      "A blogging platform with posts, followers, comments, media uploads, and real-time updates.",
    img: stock3,
    link: "#",
  },
  {
    id: 4,
    title: "Virtual Classroom",
    tech: "Unity | GPT-4 | Plugins",
    description:
      "Interactive classroom with GPT-4 chatbot, PDF viewer, and collaborative whiteboard.",
    img: stock4,
    link: "#",
  },
  {
    id: 5,
    title: "Weather Forecast App",
    tech: "Blazor | Weather API | MongoDB",
    description:
      "Accurate 14-day forecasts with login, location-based updates, and responsive design.",
    img: stock5,
    link: "#",
  },
  {
    id: 6,
    title: "Virtual Herbal Garden",
    tech: "Unity | Assets | Plugins",
    description:
      "Simulation app for medicinal plants: plant seeds, monitor growth, and learn preparation.",
    img: stock6,
    link: "#",
  },
];

const PortfolioCarousel = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  // Auto-slide every 4s with a pause gap
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % projects.length);
    }, 4000); // 4s per slide
    return () => clearInterval(interval);
  }, []);

  return (
    <section id="work" className="portfolio-mf sect-pt4 route">
      <div className="container text-center">
        <h3 className="title-a">Portfolio</h3>
        <p className="subtitle-a">A selection of my recent projects</p>
        <div className="line-mf mb-4"></div>

        <div style={{ overflow: "hidden", width: "100%" }}>
          <div
            className="slider"
            style={{
              display: "flex",
              transition: "transform 1s ease-in-out",
              transform: `translateX(-${currentIndex * (100 / 3)}%)`, // show 3 at once
              width: `${(projects.length / 3) * 100}%`,
            }}
          >
            {projects.map((project, index) => (
              <div
                key={index}
                style={{
                  flex: "0 0 calc(100% / 3)", // 3 cards visible at once
                  margin: "0 15px",
                  border: "1px solid #ccc",
                  borderRadius: "10px",
                  background: "#fff",
                  padding: "15px",
                  display: "flex",
                  flexDirection: "column",
                  justifyContent: "space-between",
                  boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
                  transition: "transform 0.3s",
                }}
                className="project-card"
              >
                <div
                  className="img-container"
                  style={{ overflow: "hidden", borderRadius: "8px" }}
                >
                  <img
                    src={project.img}
                    alt={project.title}
                    style={{
                      width: "100%",
                      height: "160px",
                      objectFit: "cover",
                      transition: "transform 0.5s ease",
                    }}
                    className="project-image"
                  />
                </div>
                <div>
                  <h5>{project.title}</h5>
                  <p style={{ fontSize: "0.85rem", color: "#555" }}>
                    {project.tech}
                  </p>
                  <p style={{ fontSize: "0.8rem", color: "#777" }}>
                    {project.description}
                  </p>
                </div>
                <a
                  href={project.link}
                  className="btn btn-sm btn-primary mt-2"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  View Project
                </a>
              </div>
            ))}
          </div>
        </div>
      </div>

      <style>
        {`
          .project-card:hover .project-image {
            transform: scale(1.1);
          }
          .project-card:hover {
            transform: scale(1.03);
            box-shadow: 0 8px 20px rgba(0,0,0,0.2);
          }
        `}
      </style>
    </section>
  );
};

export default PortfolioCarousel;
