import React from "react";
import Slider from "react-slick";
import {
  FaAws,
  FaDocker,
  FaGitAlt,
  FaJava,
  FaWordpress,
} from "react-icons/fa";
import {
  SiTerraform,
  SiGrafana,
  SiMongodb,
  SiBlazor,
} from "react-icons/si";

import myImage from "../img/myImage.png";
import "./About.css";

class About extends React.Component {
  constructor() {
    super();
    this.sliderRef = React.createRef(); // reference to slider
    this.state = {
      skills: [
        { id: "aws", name: "AWS", icon: <FaAws color="#FF9900" /> },
        { id: "devops", name: "Git/GitHub", icon: <FaGitAlt color="#F05032" /> },
        { id: "docker", name: "Docker", icon: <FaDocker color="#2496ED" /> },
        { id: "monitoring", name: "Grafana", icon: <SiGrafana color="#F46800" /> },
        { id: "iac", name: "Terraform", icon: <SiTerraform color="#7B42BC" /> },
        { id: "languages", name: "Java", icon: <FaJava color="#007396" /> },
        { id: "web", name: "Blazor", icon: <SiBlazor color="#512BD4" /> },
        { id: "db", name: "MongoDB", icon: <SiMongodb color="#47A248" /> },
        { id: "cms", name: "WordPress", icon: <FaWordpress color="#21759B" /> },
      ],
      about_me: [
        {
          id: "intro",
          content:
            "Iâ€™m Kavinraj S.K, a Computer Science undergraduate at Kumaraguru College of Technology with a strong foundation in Cloud, DevOps, and Full Stack Development."
        },
        {
          id: "work",
          content:
            "Currently working as a Software Engineer Trainee at Wise Work, where I build responsive full stack applications using Blazor, MudBlazor, and C#."
        },
        {
          id: "vision",
          content:
            "Iâ€™m passionate about creating scalable applications, automating workflows, and leveraging cloud-native tools to solve real-world problems."
        }
      ]
    };
  }

  handleMouseEnter = () => {
    if (this.sliderRef.current) {
      this.sliderRef.current.slickPause();
    }
  };

  handleMouseLeave = () => {
    if (this.sliderRef.current) {
      this.sliderRef.current.slickPlay();
    }
  };

  render() {
    const settings = {
      infinite: true,
      autoplay: true,
      autoplaySpeed: 2000,
      speed: 2000,
      slidesToShow: 5,
      slidesToScroll: 1,
      arrows: false,
      pauseOnHover: false,
      cssEase: "linear",
    };

    return (
      <section id="about" className="about-mf sect-pt4 route">
        <div className="container-fluid">{/* full width slider */}
          <div className="row">
            <div className="col-sm-12">
              <div className="box-shadow-full modern-card">
                <div className="row">
                  {/* Profile Column */}
                  <div className="col-md-5 text-center">
                    <div className="about-img mb-4">
                      <img
                        src={myImage}
                        alt="Profile"
                        className="img-fluid rounded-circle shadow-lg profile-img"
                      />
                    </div>
                  </div>

                  {/* About Me + Skills */}
                  <div className="col-md-7">
                    <div className="about-me pt-4 pt-md-0">
                      <div className="title-box-2 mb-4">
                        <h3 className="title-left">About Me</h3>
                      </div>
                      {this.state.about_me.map((content) => (
                        <p className="lead text-muted" key={content.id}>
                          {content.content}
                        </p>
                      ))}
                    </div>

                    {/* ðŸ”¹ Skills Slider Only */}
                    <div className="skills-showcase mt-4">
                      <h5 className="title-s mb-3">Skills & Tools</h5>
                      <Slider ref={this.sliderRef} {...settings} className="skills-slider mb-4">
                        {this.state.skills.map((skill) => (
                          <div
                            key={skill.id}
                            className="skill-slide"
                            onMouseEnter={this.handleMouseEnter}
                            onMouseLeave={this.handleMouseLeave}
                          >
                            <div className="skill-flip">
                              {/* Front Side */}
                              <div className="skill-front">
                                <span className="skill-icon-large">{skill.icon}</span>
                              </div>
                              {/* Back Side */}
                              <div className="skill-back">
                                <p>{skill.name}</p>
                              </div>
                            </div>
                          </div>
                        ))}
                      </Slider>
                    </div>
                    {/* End Slider */}
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    );
  }
}

export default About;
