import React, { useState, useEffect } from "react";
import "./Navbar.css"; // custom styling

const Navbar = () => {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isOpen, setIsOpen] = useState(false);
  const [activeSection, setActiveSection] = useState("#home");

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);

      // highlight active section
      const sections = ["#home", "#about", "#work", "#contact"];
      for (let id of sections) {
        const el = document.querySelector(id);
        if (el) {
          const rect = el.getBoundingClientRect();
          if (rect.top <= 150 && rect.bottom >= 150) {
            setActiveSection(id);
            break;
          }
        }
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const scrollToSection = (e, id) => {
    e.preventDefault();
    const target = document.querySelector(id);
    if (target) {
      target.scrollIntoView({ behavior: "smooth", block: "start" });
      setIsOpen(false);
    }
  };

  return (
    <nav
      className={`navbar fixed-top navbar-expand-md ${
        isScrolled ? "navbar-reduce" : "navbar-trans"
      }`}
    >
      <div className="container">
        {/* Logo / Brand */}
        <a className="navbar-brand" href="#home" onClick={(e) => scrollToSection(e, "#home")}>
          <span className="brand-highlight">Kavin</span> Portfolio
        </a>

        {/* Toggle Button */}
        <button
          className={`navbar-toggler ${isOpen ? "" : "collapsed"}`}
          type="button"
          onClick={() => setIsOpen(!isOpen)}
          aria-expanded={isOpen}
          aria-label="Toggle navigation"
        >
          <span></span>
          <span></span>
          <span></span>
        </button>

        {/* Menu Items */}
        <div
          className={`navbar-collapse collapse justify-content-end ${
            isOpen ? "show" : ""
          }`}
          id="navbarDefault"
        >
          <ul className="navbar-nav">
            {[
              { id: "#home", label: "Home" },
              { id: "#about", label: "About" },
              { id: "#work", label: "Work" },
              { id: "#contact", label: "Contact" },
            ].map((item) => (
              <li className="nav-item" key={item.id}>
                <a
                  className={`nav-link js-scroll ${
                    activeSection === item.id ? "active" : ""
                  }`}
                  href={item.id}
                  onClick={(e) => scrollToSection(e, item.id)}
                >
                  {item.label}
                </a>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
